INSERT INTO media (title, description, media_type, release_year, age_restriction, creator_id) VALUES
                                                                                                  ('The Dark Knight Rises', 'Eight years after the Joker''s reign of anarchy, Batman is forced to return from his exile to save Gotham City from the brutal terrorist Bane.', 'movie', 2012, 13, 1),
                                                                                                  ('Inception', 'A thief who steals corporate secrets through dream-sharing technology is given the inverse task of planting an idea into a CEO''s mind.', 'movie', 2010, 13, 1),
                                                                                                  ('Interstellar', 'A team of explorers travel through a wormhole in space in an attempt to ensure humanity''s survival.', 'movie', 2014, 10, 2),
                                                                                                  ('The Matrix', 'A computer hacker learns about the true nature of reality and his role in the war against its controllers.', 'movie', 1999, 16, 3),
                                                                                                  ('Pulp Fiction', 'The lives of two mob hitmen, a boxer, a gangster and his wife intertwine in four tales of violence and redemption.', 'movie', 1994, 18, 4),
                                                                                                  ('Forrest Gump', 'The story of a simple man who witnesses and influences several major historical events in 20th century America.', 'movie', 1994, 12, 5),
                                                                                                  ('The Shawshank Redemption', 'Two imprisoned men bond over a number of years, finding solace and eventual redemption through acts of common decency.', 'movie', 1994, 16, 6),
                                                                                                  ('Fight Club', 'An insomniac office worker and a soap salesman build a global organization to help vent male aggression.', 'movie', 1999, 18, 7),
                                                                                                  ('The Lord of the Rings: The Fellowship of the Ring', 'A young hobbit must destroy a powerful ring to save Middle-earth from the Dark Lord Sauron.', 'movie', 2001, 12, 8),
                                                                                                  ('Gladiator', 'A former Roman General sets out to exact vengeance against the corrupt emperor who murdered his family and sent him into slavery.', 'movie', 2000, 16, 9),
                                                                                                  ('Jurassic Park', 'A pragmatic paleontologist touring an almost complete theme park is tasked with protecting a couple of kids after a power failure.', 'movie', 1993, 12, 10),
                                                                                                  ('The Silence of the Lambs', 'A young FBI cadet must receive the help of an incarcerated and manipulative cannibal killer to help catch another serial killer.', 'movie', 1991, 18, 11),
                                                                                                  ('Schindler''s List', 'In German-occupied Poland during World War II, Oskar Schindler gradually becomes concerned for his Jewish workforce.', 'movie', 1993, 16, 12),
                                                                                                  ('Goodfellas', 'The story of Henry Hill and his life in the mob, covering his relationship with his wife Karen and his mob partners.', 'movie', 1990, 18, 13),
                                                                                                  ('Avatar', 'A paraplegic Marine dispatched to the moon Pandora on a unique mission becomes torn between following his orders and protecting the world he feels is his home.', 'movie', 2009, 12, 14);
INSERT INTO media (title, description, media_type, release_year, age_restriction, creator_id) VALUES
                                                                                                  ('Breaking Bad', 'A high school chemistry teacher diagnosed with lung cancer turns to manufacturing meth to secure his family''s future.', 'series', 2008, 18, 1),
                                                                                                  ('Game of Thrones', 'Nine noble families fight for control over the lands of Westeros, while an ancient enemy returns after being dormant for millennia.', 'series', 2011, 18, 2),
                                                                                                  ('Stranger Things', 'When a young boy disappears, his mother, a police chief and his friends must confront terrifying supernatural forces to get him back.', 'series', 2016, 14, 3),
                                                                                                  ('The Office', 'A mockumentary on a group of typical office workers, where the workday consists of ego clashes, inappropriate behavior, and tedium.', 'series', 2005, 12, 4),
                                                                                                  ('Friends', 'Follows the personal and professional lives of six twenty to thirty year-olds living in Manhattan.', 'series', 1994, 12, 5),
                                                                                                  ('The Mandalorian', 'The travels of a lone bounty hunter in the outer reaches of the galaxy, far from the authority of the New Republic.', 'series', 2019, 12, 6),
                                                                                                  ('Chernobyl', 'A dramatization of the true story of one of the worst man-made catastrophes in history.', 'series', 2019, 16, 7),
                                                                                                  ('The Witcher', 'Geralt of Rivia, a solitary monster hunter, struggles to find his place in a world where people often prove more wicked than beasts.', 'series', 2019, 18, 8),
                                                                                                  ('Peaky Blinders', 'A gangster family epic set in 1900s England, centered on a gang who sew razor blades in the peaks of their caps.', 'series', 2013, 18, 9),
                                                                                                  ('The Crown', 'Follows the political rivalries and romance of Queen Elizabeth II''s reign and the events that shaped the second half of the 20th century.', 'series', 2016, 12, 10),
                                                                                                  ('Sherlock', 'A modern update finds the classic detective in 21st century London.', 'series', 2010, 14, 11),
                                                                                                  ('Dark', 'A family saga with a supernatural twist, set in a German town, where the disappearance of two children exposes complex relationships.', 'series', 2017, 16, 12),
                                                                                                  ('Westworld', 'Set at the intersection of the near future and the reimagined past, it explores a world in which every human appetite can be indulged.', 'series', 2016, 18, 13),
                                                                                                  ('The Boys', 'A group of vigilantes set out to take down corrupt superheroes who abuse their superpowers.', 'series', 2019, 18, 14),
                                                                                                  ('Narcos', 'A chronicled look at the criminal exploits of Colombian drug lord Pablo Escobar.', 'series', 2015, 18, 15);
INSERT INTO media (title, description, media_type, release_year, age_restriction, creator_id) VALUES
                                                                                                  ('The Last of Us', 'A hardened survivor and a young girl must work together to survive in a post-apocalyptic United States.', 'game', 2013, 18, 1),
                                                                                                  ('Red Dead Redemption 2', 'America, 1899. Arthur Morgan must deal with the decline of the wild west while trying to survive against government forces.', 'game', 2018, 18, 2),
                                                                                                  ('God of War', 'After wiping out the gods of Mount Olympus, Kratos moves to the Norse realm where he and his son must confront Norse gods.', 'game', 2018, 17, 3),
                                                                                                  ('The Witcher 3: Wild Hunt', 'Geralt of Rivia sets out one last time to slay beasts, bargain with kings, and navigate a war-torn land.', 'game', 2015, 18, 4),
                                                                                                  ('Elden Ring', 'An action RPG set in the Lands Between, a realm ruled by demigods. Rise, Tarnished, and be guided by grace.', 'game', 2022, 16, 5),
                                                                                                  ('Minecraft', 'A sandbox game that allows players to build with a variety of different blocks in a 3D procedurally generated world.', 'game', 2011, 7, 6),
                                                                                                  ('Grand Theft Auto V', 'Three criminals attempt to commit heists while under pressure from a corrupt government agency.', 'game', 2013, 18, 7),
                                                                                                  ('Zelda: Breath of the Wild', 'Link awakens from a hundred-year slumber to defeat Calamity Ganon before it can destroy the kingdom of Hyrule.', 'game', 2017, 10, 8),
                                                                                                  ('Horizon Zero Dawn', 'In a post-apocalyptic world, Aloy must discover her origins while battling the machines that roam the land.', 'game', 2017, 16, 9),
                                                                                                  ('Ghost of Tsushima', 'Jin Sakai must protect his homeland during the first Mongol invasion of Japan in 1274.', 'game', 2020, 18, 10),
                                                                                                  ('Cyberpunk 2077', 'An open-world action-adventure set in Night City, a megalopolis obsessed with power, glamour and body modification.', 'game', 2020, 18, 11),
                                                                                                  ('Hollow Knight', 'A challenging 2D action-adventure set in a vast ruined kingdom of insects and heroes.', 'game', 2017, 10, 12),
                                                                                                  ('Hades', 'Defy the god of the dead as you hack and slash out of the Underworld in this rogue-like dungeon crawler.', 'game', 2020, 12, 13),
                                                                                                  ('Sekiro: Shadows Die Twice', 'In the late 1500s, a shinobi must rescue his young lord and exact revenge on his enemies.', 'game', 2019, 18, 14),
                                                                                                  ('Portal 2', 'Trapped in an abandoned science facility, you must solve physics-based puzzles to escape with the help of a sarcastic AI.', 'game', 2011, 10, 15);
